import React, { useState } from 'react';

function SizeCalculator() {
    const [sizes, setSizes] = useState([])
    const [types, setTypes] = useState([])

    const [addSize, setAddSize] = useState(false)
    const [addType, setAddType] = useState(false)

    const [temp, setTemp] = useState(0)

    const [defSize, setDefSize] = useState({})
    const [delSize, setDelSize] = useState({})

    const onAddSize = () => {
        if(addSize) {
            sizes.push(temp)
            setAddSize(false);
        }
        else {
            setAddSize(true);
        }
    }

    const onAddType = () => {
        if(addType) {
            types.push(temp)
            defSize[temp] = 0;
            delSize[temp] = 0;
            setAddType(false);
        }
        else {
            setAddType(true);
        }
    }

    const onInputSize = (e) => {
        setTemp(e.target.value);
    }

    const onChangeDefSize = (type) => {
        return (e) => {
            const s = defSize;
            s[type] = e.target.value;
            setDefSize(s);
        }
    }

    const onChangeDelSize = (type) => {
        return (e) => {
            const s = delSize;
            s[type] = e.target.value;
            setDelSize(s);
        }
    }


    return (
        <div className="frame">
            <div>
                <table>
                    <tr>
                        <th>사이즈</th>
                        {types.map((v) => {return <th>{v}</th>})}
                        {(() => {
                            if(addType) {
                                return <div>
                                    <input onChange={onInputSize}></input>
                                    <button onClick={onAddType}>완료</button>
                                    </div>
                            }
                            else {
                                return <button onClick={onAddType}>추가</button>;
                            }
                        })()}
                    </tr>
                    {sizes.map((v) => {return (
                        <tr>
                            <th>{v}</th>
                            {types.map((v) => {console.log((defSize[v]+delSize[v])); return <th>{(defSize[v]+delSize[v])}</th>})}
                        </tr>
                    )})}
                    <tr>
                        <th>기본사이즈</th>
                        {types.map((v) => {return <th><input onChange={onChangeDefSize(v)}></input></th>})}
                    </tr>
                    <tr>
                        <th>증가량</th>
                        {types.map((v) => {return <th><input onChange={onChangeDelSize(v)}></input></th>})}
                    </tr>
                    <tr><th>
                        {(() => {
                            if(addSize) {
                                return <div>
                                    <input onChange={onInputSize}></input>
                                    <button onClick={onAddSize}>완료</button>
                                    </div>
                            }
                            else {
                                return <button onClick={onAddSize}>추가</button>;
                            }
                        })()}</th></tr>
                </table>
            </div>
        </div>
    );
}
export default SizeCalculator;
