import React, { useState } from 'react';
import './SizeCalculator.css';


function SizeCalculator() {
    const [sizes, setSizes] = useState([])
    const [types, setTypes] = useState([])

    const [addSize, setAddSize] = useState(false)
    const [addType, setAddType] = useState(false)

    const [temp, setTemp] = useState(0)

    const [defSize, setDefSize] = useState({})
    const [delSize, setDelSize] = useState({})

    const onAddSize = () => {
        // if(addSize) {
            if (temp != -1) {
                if(temp.includes('~')) {
                    const [from, to] = temp.split('~');
                    console.log(from)
                    console.log(to)
                    for(let i=parseInt(from);i<=parseInt(to);i+=10) {
                        console.log(i);
                        sizes.push(i)
                    }
                }
                else 
                    sizes.push(temp)
            }
        //     setAddSize(false);
        // }
        // else {
        //     setAddSize(true);
        //     setTemp(-1);
        // }
    }

    const onAddType = () => {
        // if(addType) {
            if (temp != -1) {
                if(temp.includes(',')) {
                    const ts = temp.split(',');
                    for(const type of ts) {
                        types.push(type)
                        setDefSize({
                            ...defSize,
                            [type]: 0
                        })
                        setDelSize({
                            ...delSize,
                            [type]: 0
                        })
                    }
                }
                else {
                    types.push(temp)
                    setDefSize({
                        ...defSize,
                        [temp]: 0
                    })
                    setDelSize({
                        ...delSize,
                        [temp]: 0
                    })
                }
            }
            // setAddType(false);
        // }
        // else {
        //     setAddType(true);
        //     setTemp(-1);
        // }
    }

    const onInputSize = (e) => {
        setTemp(e.target.value);
    }

    const onChangeDefSize = (type) => {
        return (e) => {
            setDefSize({
                ...defSize,
                [type]: e.target.value
            })
        }
    }

    const onChangeDelSize = (type) => {
        return (e) => {
            setDelSize({
                ...delSize,
                [type]: e.target.value
            })
        }
    }

    const onEnterSize = (e) => {
        console.log(e);
        if(e.keyCode == 13)
        {
            onAddSize();
        }
    }

    const onEnterType = (e) => {
        console.log(e);
        if(e.keyCode == 13)
        {
            onAddType();
        }
    }


    return (
        <div className="frame">
            <div>
                <table>
                    <tr>
                        <th>사이즈</th>
                        {types.map((v) => {return <th>{v}</th>})}
                        <th>
                        {(() => {
                            // if(addType) {
                                return <div>
                                    <input onChange={onInputSize} onKeyDown={onEnterType}></input>
                                    <button onClick={onAddType}>추가</button>
                                    </div>
                            // }
                            // else {
                            //     return <button onClick={onAddType}>추가</button>;
                            // }
                        })()}
                        </th>
                    </tr>
                    {sizes.map((row, i) => {return (
                        <tr>
                            <th>{row}</th>
                            {types.map((v) => {return <th>{parseFloat(defSize[v])+(parseFloat(delSize[v])*(i))}</th>})}
                        </tr>
                    )})}
                    <tr><th>
                        {(() => {
                            // if(addSize) {
                                return <div>
                                    <input onChange={onInputSize} onKeyDown={onEnterSize}></input>
                                    <button onClick={onAddSize}>추가</button>
                                    </div>
                            // }
                            // else {
                            //     return <button onClick={onAddSize}>추가</button>;
                            // }
                        })()}</th></tr>
                    <br/>
                    <tr>
                        <th>기본사이즈</th>
                        {types.map((v) => {return <th><input onChange={onChangeDefSize(v)}></input></th>})}
                    </tr>
                    <tr>
                        <th>증가량</th>
                        {types.map((v) => {return <th><input onChange={onChangeDelSize(v)}></input></th>})}
                    </tr>
                </table>
            </div>
        </div>
    );
}
export default SizeCalculator;
