import React, { useState } from 'react';

function SizeCalculator() {
    const [sizes, setSizes] = useState([1,2,3])
    const [types, setTypes] = useState(['a', 'b'])

    const [addSize, setAddSize] = useState(false)

    const [defSize, setDefSize] = useState({}) // key : type
    const [delSize, setDelSize] = useState({}) // key : type

    const onAddSize = () => {
        if(addSize) {
            setAddSize(false);
        }
        else {
            setAddSize(true);
        }
    }

    const onInputSize = () => {

    }

    return (
        <div className="frame">
            <div>
                <table>
                    <tr>
                        <th>사이즈</th>
                        <button onClick={onAddSize}>추가</button>
                    </tr>
                    {sizes.map((v) => {return (
                        <tr>
                            <th>{v}</th>
                            {types.map((v) => {return <th>값</th>})}
                        </tr>
                    )})}
                    <tr>
                        <th>기본사이즈</th>
                        {types.map((v) => {return <th><input></input></th>})}
                    </tr>
                    <tr>
                        <th>증가량</th>
                        {types.map((v) => {return <th><input></input></th>})}
                    </tr>
                    <tr><th>
                        {(() => {
                            if(addSize) {
                                return <div>
                                    <input></input>
                                    <button onClick={onAddSize}>완료</button>
                                    </div>
                            }
                            else {
                                return <button onClick={onAddSize}>추가</button>;
                            }
                        })()}</th></tr>
                </table>
            </div>
        </div>
    );
}
export default SizeCalculator;
